const fs = require\('fs'\);
const target = 'C:/Claude_AI/fms-web/src/app/logis/oms/service-order/page.tsx';

let c = `
  const fetchOrders = useCallback\(async \(\) => {
    try {
      setLoading\(true\);
      const params = new URLSearchParams\(\);
      if \(searchFilters.status\) params.append\('status', searchFilters.status\);
      if \(searchFilters.executionModule\) params.append\('executionModule', searchFilters.executionModule\);
      if \(searchFilters.fromDate\) params.append\('fromDate', searchFilters.fromDate\);
      if \(searchFilters.toDate\) params.append\('toDate', searchFilters.toDate\);

      const response = await fetch\(\\`/api/oms/service-order?\\${params}\\`\);
      const result = await response.json\(\);
      if \(result.success\) {
        setOrders\(result.data\);
      }
    } catch \(error\) {
      console.error\('Error fetching orders:', error\);
    } finally {
      setLoading\(false\);
    }
  }, [searchFilters]\);

  const fetchOrderTypes = async \(\) => {
    try {
      const response = await fetch\('/api/oms/order-type?isActive=true'\);
      const result = await response.json\(\);
      if \(result.success\) {
        setOrderTypes\(result.data\);
      }
    } catch \(error\) {
      console.error\('Error fetching order types:', error\);
    }
  };

  useEffect\(\(\) => {
    fetchOrders\(\);
    fetchOrderTypes\(\);
  }, [fetchOrders]\);

  const resetForm = \(\) => {
    setFormData\({
      co_number: '',
      order_type_code: '',
      biz_type: 'FORWARDING',
      customer_code: '',
      customer_name: '',
      shipper_name: '',
      consignee_name: '',
      pol: '',
      pod: '',
      etd: '',
      eta: '',
      cargo_type: '',
      commodity: '',
      quantity: 0,
      weight: 0,
      volume: 0,
      incoterms: '',
      execution_module: '',
      control_type: '',
      auto_release: false,
      status: 'DRAFT',
      remarks: '',
    }\);
    setEditingOrder\(null\);
  };

  const handleSave = async \(\) => {
    try {
      const url = '/api/oms/service-order';
      const method = editingOrder ? 'PUT' : 'POST';
      const body = editingOrder ? { ...formData, id: editingOrder.id } : formData;

      const response = await fetch\(url, {
        method,
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify\(body\),
      }\);

      const result = await response.json\(\);
      if \(result.success\) {
        alert\(result.message\);
        setIsModalOpen\(false\);
        resetForm\(\);
        fetchOrders\(\);
      } else {
        alert\('오류: ' + result.error\);
      }
    } catch \(error\) {
      console.error\('Error saving order:', error\);
      alert\('저장 중 오류가 발생했습니다.'\);
    }
  };

  const handleDelete = async \(id: number\) => {
    if \(!confirm\('정말 삭제하시겠습니까?'\)\) return;
    try {
      const response = await fetch\(\\`/api/oms/service-order?id=\\${id}\\`, { method: 'DELETE' }\);
      const result = await response.json\(\);
      if \(result.success\) {
        alert\(result.message\);
        fetchOrders\(\);
      } else {
        alert\('오류: ' + result.error\);
      }
    } catch \(error\) {
      console.error\('Error deleting order:', error\);
      alert\('삭제 중 오류가 발생했습니다.'\);
    }
  };

  const handleBulkDelete = async \(\) => {
    if \(selectedIds.size === 0\) {
      alert\('삭제할 항목을 선택해주세요.'\);
      return;
    }
    if \(!confirm\(\\`선택한 \\${selectedIds.size}건을 삭제하시겠습니까?\\`\)\) return;
    try {
      for \(const id of selectedIds\) {
        await fetch\(\\`/api/oms/service-order?id=\\${id}\\`, { method: 'DELETE' }\);
      }
      alert\('삭제가 완료되었습니다.'\);
      setSelectedIds\(new Set\(\)\);
      fetchOrders\(\);
    } catch \(error\) {
      console.error\('Error bulk deleting:', error\);
      alert\('삭제 중 오류가 발생했습니다.'\);
    }
  };
`;
fs.appendFileSync\(target, c, 'utf-8'\);
console.log\('Part 3 appended'\);
JSEOF)

