const fs = require\('fs'\);
const target = 'C:/Claude_AI/fms-web/src/app/logis/oms/service-order/page.tsx';

let c = `
  const handleEdit = \(order: ServiceOrder\) => {
    setEditingOrder\(order\);
    setFormData\({
      co_number: order.co_number || '',
      order_type_code: order.order_type_code || '',
      biz_type: order.biz_type || 'FORWARDING',
      customer_code: order.customer_code || '',
      customer_name: order.customer_name || '',
      shipper_name: order.shipper_name || '',
      consignee_name: order.consignee_name || '',
      pol: order.pol || '',
      pod: order.pod || '',
      etd: order.etd ? order.etd.substring\(0, 10\) : '',
      eta: order.eta ? order.eta.substring\(0, 10\) : '',
      cargo_type: order.cargo_type || '',
      commodity: order.commodity || '',
      quantity: order.quantity || 0,
      weight: order.weight || 0,
      volume: order.volume || 0,
      incoterms: order.incoterms || '',
      execution_module: order.execution_module || '',
      control_type: order.control_type || '',
      auto_release: order.auto_release || false,
      status: order.status || 'DRAFT',
      remarks: order.remarks || '',
    }\);
    setIsModalOpen\(true\);
  };

  const handleSelectAll = \(checked: boolean\) => {
    if \(checked\) {
      setSelectedIds\(new Set\(orders.map\(o => o.id\)\)\);
    } else {
      setSelectedIds\(new Set\(\)\);
    }
  };

  const handleSelect = \(id: number, checked: boolean\) => {
    const newSet = new Set\(selectedIds\);
    if \(checked\) { newSet.add\(id\); } else { newSet.delete\(id\); }
    setSelectedIds\(newSet\);
  };

  const getStatusBadge = \(status: string\) => {
    const statusOption = STATUS_OPTIONS.find\(s => s.value === status\);
    return \(
      <span className={\\`px-2 py-1 rounded-full text-xs text-white \\${statusOption?.color || 'bg-gray-500'}\\`}>
        {statusOption?.label || status}
      </span>
    \);
  };

  const getModuleBadge = \(module: string\) => {
    const colors: Record<string, string> = { FW: 'bg-blue-600', TM: 'bg-green-600', WM: 'bg-purple-600' };
    return module ? \(
      <span className={\\`px-2 py-1 rounded-full text-xs text-white \\${colors[module] || 'bg-gray-500'}\\`}>{module}</span>
    \) : null;
  };

  const handleSearchReset = \(\) => {
    setSearchFilters\({ status: '', executionModule: '', fromDate: '', toDate: '' }\);
  };
`;
